mapper.select:
SELECT ID ,COMPANY_CODE ,ADMIN_ID ,TITLE ,CONTENT ,CATEGORY ,CASE WHEN CATEGORY = 'E' THEN '긴급' WHEN CATEGORY = 'N' THEN '일반' WHEN CATEGORY = 'W' THEN '워닝' ELSE '일반' END AS CATEGORY_NAME ,DEPT_CODE ,DEPT_NAME ,DEPT_CONFIRM_COUNT ,DEPT_TOTAL ,DEPT_CONFIRM_YN ,CREATED_DATETIME ,IF(DATEDIFF(DATE_FORMAT(NOW(), '%Y-%m-%d'), DATE_FORMAT(UPDATED_DATETIME, '%Y-%m-%d')) = 0, 'Y', 'N') AS NEW_YN ,DATE_FORMAT(UPDATED_DATETIME, '%Y-%m-%d') AS UPDATED_DATETIME ,REPLACE(REPLACE(DATE_FORMAT(UPDATED_DATETIME, '%Y.%m.%d %p %h:%i'),'AM', '오전'),'PM','오후') AS CONVERSION_UPDATED_DATETIME FROM DP_ADV_ADMIN_NOTICE WHERE ADMIN_ID = #{adminId}
AND CATEGORY = #{category}
AND DATE_FORMAT(UPDATED_DATETIME, '%Y-%m-%d')>=#{startDate} AND DATE_FORMAT(UPDATED_DATETIME, '%Y-%m-%d')<=#{endDate}
ORDER BY DATE_FORMAT(UPDATED_DATETIME, '%Y.%m.%d %p %h:%i') DESC LIMIT #{offset}, #{limit}

   SELECT 
     COUNT(*)
   FROM DP_ADV_ADMIN_NOTICE
   WHERE ADMIN_ID = #{adminId}


	   AND CATEGORY = #{category}



     AND DATE_FORMAT(UPDATED_DATETIME, '%Y-%m-%d')>=#{startDate} 
     AND DATE_FORMAT(UPDATED_DATETIME, '%Y-%m-%d')<=#{endDate}

mapper.update:
UPDATE DP_ADV_ADMIN_NOTICE SET
  TITLE = #{title}
 ,CONTENT = #{content}
 ,CATEGORY = #{category}
 ,UPDATED_DATETIME = NOW()
WHERE ID = #{id}
AND ADMIN_ID = #{adminId}

mapper.delete:
DELETE FROM DP_ADV_ADMIN_NOTICE
WHERE ID = #{id}
AND ADMIN_ID =#{adminId}
