mapper.update:
	UPDATE DP_ADV_CALL_DATA
	   SET CUSTOMER_HIGHLIGHT =


		    1


		    0



     CUSTOMER_ID = #{customerId}
	 AND ADVISOR_ID = #{advisorId}

mapper.select:
		SELECT 
               IFNULL (
                       (SELECT AGENT_NAME
                          FROM DP_ADV_AGENT 
                         WHERE AGENT_ID = #{agentId}
                      ), '미등록') AS AGENT_NAME
             , IFNULL (
                       (SELECT AGENT_TITLE 
                          FROM DP_ADV_AGENT 
                         WHERE AGENT_ID = #{agentId}
                      ), '미등록') AS AGENT_TITLE

SELECT IFNULL(NULLIF(DAD.DEPT_CODE, ''), '부서 코드 없음') AS DEPT_CODE , IFNULL(NULLIF(DAD.DEPT_NAME, ''), '부서명 없음') AS DEPT_NAME , IFNULL(NULLIF(DAD.COMPANY_CODE, ''), '회사 코드 없음') AS COMPANY_CODE , IFNULL(NULLIF(DAA.AGENT_ID, ''), '상담원 아이디 없음') AS AGENT_ID , IFNULL(NULLIF(DAA.AGENT_NAME, ''), '상담원 이름 없음') AS AGENT_NAME , IFNULL(NULLIF(DAA.EXT, ''), '내선번호 없음') AS EXT , IFNULL(NULLIF(DAA.AGENT_TITLE, ''), '상담원 직급 없음') AS AGENT_TITLE , IFNULL(NULLIF(DAA.IS_ADMIN , ''), '관리자 여부 정보 없음') AS IS_ADMIN FROM DP_ADV_DEPT DAD LEFT JOIN DP_ADV_AGENT DAA USING (DEPT_CODE)
COMPANY_CODE = #{app_name}
AND DEPT_CODE = #{center}
AND DEPT_CODE = #{dept}
ORDER BY DAD.DEPT_CODE ASC , DAA.IS_ADMIN DESC , DAA.AGENT_TITLE DESC

SELECT IFNULL(NULLIF(DASM.CALL_ID, ''), '통화 고유번호 없음') AS CALL_ID , IFNULL(NULLIF(DASM.CALL_START_TIME, ''), '통화 시작 시간 기록 없음') AS CALL_START_TIME , IFNULL(NULLIF(DASM.CALL_END_TIME, ''), '통화 종료 시간 기록 없음') AS CALL_END_TIME , IFNULL(MINUTE(TIMEDIFF(DASM.CALL_END_TIME, DASM.CALL_START_TIME)), '0') AS CALL_TIME , IFNULL(NULLIF(DASM.CUST_ID, ''), '고객 아이디 없음') AS CUST_ID , IFNULL(NULLIF(DASM.CUST_NAME, ''), '고객 이름 없음') AS CUST_NAME , IFNULL(NULLIF(DASM.EMP_ID, ''), '사원 번호 없음') AS EMP_ID , IFNULL(NULLIF(DASM.EXT, ''), '내선 번호 없음') AS EXT , IFNULL(NULLIF(DASM.REC_KEY, ''), '녹취 파일명 없음') AS REC_KEY , IFNULL(NULLIF(DASM.INPUT_TEL, ''), '인입 번호 없음') AS INPUT_TEL , IFNULL(NULLIF(DAA.AGENT_NAME, ''), '사원 이름 없음') AS AGENT_NAME , IFNULL(NULLIF(DAD.DEPT_NAME, ''), '부서명 없음') AS DEPT_NAME , IFNULL(NULLIF(DASAI.TOPIC_SENTENCE, ''), 'TOPIC_SENTENCE') AS TOPIC_SENTENCE , IFNULL(NULLIF(DASAI.MAIN_KEYWORD, ''), 'K1, K2, K3') AS MAIN_KEYWORD FROM DP_ADV_STT_META DASM LEFT JOIN DP_ADV_AGENT DAA ON DASM.EMP_ID = DAA.AGENT_ID LEFT JOIN DP_ADV_STT_ANALYSIS_INFO DASAI ON DASM.REC_KEY = DASAI.REC_KEY LEFT JOIN DP_ADV_DEPT DAD ON DAA.DEPT_CODE = DAD.DEPT_CODE
DASM.CUST_ID = #{custId}
ORDER BY DASM.CALL_ID DESC
LIMIT ${limit}
