mapper.update:
UPDATE BOOK_MARK SET
  TITLE = #{title}
 ,CONTENTS = #{contents}
 ,GROUP_ID = #{groupId}
 ,MOD_DT = NOW()
WHERE ID = #{id}

mapper.delete:
DELETE FROM BOOK_MARK
WHERE ADV_ID = #{advId}
AND ID = #{id}

