mapper.select:
SELECT * FROM ( SELECT A.ID ,A.ADV_ID ,A.TITLE ,A.SUB_TITLE ,A.CONTENTS ,A.TYPE ,A.GROUP_ID ,B.TITLE AS GROUP_TITLE ,B.BASIC_GROUP_YN ,DATE_FORMAT(A.REG_DT, '%Y-%m-%d %T') AS REG_DT FROM BOOK_MARK A LEFT JOIN BOOK_MARK_GROUP B ON A.GROUP_ID = B.ID ) AA WHERE AA.ADV_ID = #{advId}
AND AA.GROUP_ID = #{groupId}
ORDER BY GROUP_TITLE ASC

mapper.update:
UPDATE BOOK_MARK SET
  TITLE = #{title}
 ,CONTENTS = #{contents}
 ,GROUP_ID = #{groupId}
 ,MOD_DT = NOW()
WHERE ID = #{id}

mapper.delete:
DELETE FROM BOOK_MARK
WHERE ID = #{id}
AND ADV_ID = #{advId}

DELETE FROM BOOK_MARK
WHERE ADV_ID = #{advId}
AND GROUP_ID IN

   #{item}

DELETE FROM BOOK_MARK
WHERE GROUP_ID = #{id}
AND ADV_ID = #{advId}
