mapper.select:
SELECT * FROM ( SELECT A.ID ,A.USER_ID ,A.TITLE ,A.CONTENTS ,A.GROUP_ID ,B.TITLE AS GROUP_TITLE ,DATE_FORMAT(A.REG_DT, '%Y-%m-%d %T') AS REG_DT FROM BOOK_MARK A LEFT JOIN BOOK_MARK_GROUP B ON A.GROUP_ID = B.ID ) AA WHERE 1=1
AND A.USER_ID = #{userId}
ORDER BY ${sortItem} ${sortType}

mapper.update:
UPDATE BOOK_MARK SET
  TITLE = #{title}
 ,CONTENTS = #{contents}
 ,GROUP_ID = #{groupId}
 ,MOD_DT = NOW()
 ,MOD_ID = #{userId}
WHERE ID = #{id}
