mapper.select:
SELECT * FROM ( SELECT A.ID ,A.ADV_ID ,A.TITLE ,A.SUB_TITLE ,A.CONTENT ,A.CATEGORY ,CASE WHEN A.CATEGORY = 'K' THEN '지식정보' WHEN A.CATEGORY = 'S' THEN '스크립트' ELSE '기본' END AS CATEGORY_NAME ,A.GROUP_ID ,B.TITLE AS GROUP_TITLE ,B.BASIC_GROUP_YN ,DATE_FORMAT(A.REG_DT, '%Y-%m-%d %T') AS REG_DT FROM BOOKMARK A LEFT JOIN BOOKMARK_GROUP B ON A.GROUP_ID = B.ID ) AA WHERE AA.ADV_ID = #{advId}
AND AA.GROUP_ID = #{groupId}
ORDER BY GROUP_TITLE ASC

mapper.update:
UPDATE BOOKMARK SET
  TITLE = #{title}
 ,CONTENT = #{content}
 ,GROUP_ID = #{groupId}
 ,MOD_DT = NOW()
WHERE ID = #{id}

mapper.delete:
DELETE FROM BOOKMARK
WHERE ID = #{id}
AND ADV_ID = #{advId}

DELETE FROM BOOKMARK
WHERE ADV_ID = #{advId}
AND GROUP_ID IN

   #{item}

DELETE FROM BOOKMARK
WHERE GROUP_ID = #{id}
AND ADV_ID = #{advId}
