mapper.select:
SELECT * FROM ( SELECT A.ID ,A.AGENT_ID ,A.TITLE ,A.SUB_TITLE ,A.CONTENT ,A.CATEGORY ,CASE WHEN A.CATEGORY = 'K' THEN '지식정보' WHEN A.CATEGORY = 'S' THEN '스크립트' ELSE '기본' END AS CATEGORY_NAME ,A.GROUP_ID ,B.TITLE AS GROUP_TITLE ,B.BASIC_GROUP_YN ,A.CREATED_ILSI ,REPLACE(REPLACE(DATE_FORMAT(A.UPDATED_ILSI, '%p %h:%i %m-%d-%Y'),'AM', '오전'),'PM','오후') AS CONVERSION_UPDATED_ILSI FROM BOOKMARK A LEFT JOIN BOOKMARK_GROUP B ON A.GROUP_ID = B.ID ) AA WHERE AA.AGENT_ID = #{agentId}
AND AA.GROUP_ID = #{groupId}
ORDER BY GROUP_TITLE ASC

		SELECT
			COUNT(*)
		FROM BOOKMARK
		WHERE AGENT_ID = #{agentId}
 		AND TARGET_ID = #{targetId}

mapper.update:
UPDATE BOOKMARK SET
  GROUP_ID = #{groupId}
 ,UPDATED_ILSI = NOW()
WHERE ID = #{id}

mapper.delete:
DELETE FROM BOOKMARK
WHERE AGENT_ID = #{agentId}

  AND ID = #{id}


  AND TARGET_ID = #{targetId}

DELETE FROM BOOKMARK
WHERE AGENT_ID = #{agentId}
AND GROUP_ID IN

   #{item}

DELETE FROM BOOKMARK
WHERE GROUP_ID = #{id}
AND AGENT_ID = #{agentId}
