mapper.update:
UPDATE MEMO SET
  TITLE = #{title}
 ,CONTENT = #{content}
 ,GROUP_ID = #{groupId}
 ,UPDATED_DATETIME = NOW()
WHERE ID = #{id}
AND AGENT_ID = #{agentId}

mapper.delete:
DELETE FROM MEMO
WHERE ID = #{id}
AND AGENT_ID = #{agentId}

