mapper.select:
SELECT * FROM ( SELECT A.ID ,A.USER_ID ,A.TITLE ,A.CONTENTS ,A.GROUP_ID ,B.NAME AS GROUP_NAME ,DATE_FORMAT(A.CREATE_DT, '%Y-%m-%d') AS CREATE_FORMAT_DT ,A.CREATE_DT FROM MEMO A LEFT JOIN MEMO_GROUP B ON A.GROUP_ID = B.ID ) AA WHERE 1=1
AND A.USER_ID = #{userId}
ORDER BY ${sortItem} ${sortType}

SELECT * FROM MEMO
WHERE ID = #{id}
ORDER BY CREATE_DT DESC

mapper.update:
UPDATE MEMO SET
  TITLE = #{title}
 ,CONTENTS = #{contents}
 ,GROUP_ID = #{groupId}
 ,UPDATE_DT = NOW()
WHERE ID = #{id}
